    async startlv(lv) {
      this.menusound()
      this.anstime = 10
      this.nowquestion = 0
      this.msg = ''
      this.questionimg = null
      this.question = ''
      this.answer = null
      this.answersum = 0
      this.answerlist = []
      this.anslenght = 0
      this.anspos1 = null
      this.anspos2 = null
      this.endgame = false
      document.getElementById('gamemenu').style.display = 'none'
      document.getElementById('gamestage').style.display = 'block'

      const d = new Date()
      const dd = ('0' + d.getDate()).slice(-2)
      const m = ('0' + (d.getMonth() + 1)).slice(-2)
      const h = ('0' + d.getHours()).slice(-2)
      const mm = ('0' + d.getMinutes()).slice(-2)
      const access = Base64.btoa(
        Base64.btoa(
          Base64.btoa(
            'getyonhvmbtn' +
              dd +
              '' +
              h +
              '' +
              m +
              '' +
              mm +
              '' +
              d.getFullYear() +
              '' +
              d.getDay()
          )
        )
      )

      if (lv === 100) {
        this.awaitquestion = true
        this.questionlist = (
          await this.$axios.get(
            `/api/games/yesorno100/harvest-moon-back-nature/?access=${access}`
          )
        ).data
        this.answercount = 100
      } else if (lv === 10) {
        this.awaitquestion = true
        this.questionlist = (
          await this.$axios.get(
            `/api/games/yesorno10/harvest-moon-back-nature/?access=${access}`
          )
        ).data
        this.answercount = 10
      }

      if (this.questionlist) {
        this.awaitquestion = false
        this.pushquestion()
      } else {
        this.awaitquestion = false
        this.msg = 'เรียกข้อมูลคำถามไม่ได้ กรุณาลองใหม่อีกครั้ง'
      }
    },
