      this.menusound()
      if (this.answer === true) {
        this.answerlist.push(true)
        this.answersum = this.answersum + 1
      } else this.answerlist.push(false)
      if (this.questionlist.length > 0) this.pushquestion()
      else this.endgames()
    },
    no() {
      this.menusound()
      if (this.answer === false) {
        this.answerlist.push(true)
        this.answersum = this.answersum + 1
      } else this.answerlist.push(false)
      if (this.questionlist.length > 0) this.pushquestion()
      else this.endgames()
    },
    skip() {
      this.answerlist.push(false)
      if (this.questionlist.length > 0) this.pushquestion()
      else this.endgames()
    },
